	int num1=1;
	while(num1<=100) {
	System.out.print(num1+" ");
	num1++;
	}
//-------------------------------------------------------		
	int num2=101;
	while(num2>=1) {
		System.out.println(num2+" ");
		num2--;
		
		
	}
	int num3=20;
	while(num3<=100){
		System.out.print(num3+" ");
		num3+=2;
	}
	int num4=1;
